"use strict";

var fs = require('fs');
var path = require('path');




function Logger() {
	var __LOG_PATH;

	this.setLogPath = function(pathName) {
		__LOG_PATH = path.join(pathName, 'log');
	};
	this.getLogPath = function() {
		return __LOG_PATH;
	}
}

Logger.prototype.log = function(msg) {
	fs.appendFile(this.getLogPath(), msg + '\n', function(err) {
		if( err ) {
			return false;
		}
		console.log("logging...");
	});
};
	
module.exports = new Logger();
